package com.module.test.view;import android.content.Context;import android.util.AttributeSet;import android.view.ViewDebug.ExportedProperty;import android.widget.TextView;/** * 跑马灯 */public class MarqueeText extends TextView {    private boolean isStart = true;    public MarqueeText(Context context) {        super(context);    }    public MarqueeText(Context context, AttributeSet attrs) {        super(context, attrs);    }    public MarqueeText(Context context, AttributeSet attrs, int defStyle) {        super(context, attrs, defStyle);    }    @Override    @ExportedProperty(category = "focus")    public boolean isFocused() {        return isStart;    }    public boolean isStart() {        return isStart;    }    public void setStart(boolean isStart) {        this.isStart = isStart;    }}